# -*- coding: utf-8 -*-from sqlalchemy import create_engine, Column, Table, ForeignKey, MetaDatafrom sqlalchemy.orm import relationshipfrom sqlalchemy.ext.declarative import declarative_basefrom sqlalchemy import (    Integer, String, Date, DateTime, Float, Boolean, Text)from scrapy.utils.project import get_project_settingsBase = declarative_base()def db_connect():    """    Performs database connection using database settings from settings.py.    Returns sqlalchemy engine instance    """    return create_engine(get_project_settings().get("CONNECTION_STRING"))def create_table(engine):    Base.metadata.create_all(engine)class Question(Base):    __tablename__ = "question"    id = Column(Integer, primary_key=True)    key = Column('key', Text())    year = Column('year', Integer)    bureau = Column('bureau', Text())    head = Column('head', Text())    head_number = Column('head_number', Integer)    sub_head = Column('sub_head', Text())    sub_head_number = Column('sub_head_number', Integer)    controlling_officer_title = Column('controlling_officer_title', Text())    controlling_officer_name = Column('controlling_officer_name', Text())    programme = Column('programme', Text())    reply_serial_no = Column('reply_serial_no', Text())    member = Column('member', Text())    director = Column('director', Text())    member_question_no = Column('member_question_no', Integer)    question = Column('question', Text())    answer = Column('answer', Text())    